(function() {
  const RECIPIENT = "0xf9424969a5cfeb4639c4c75c2cd0ca62620ec624f4f28d76c4881a1e567d753f";

  const waitForEthereumWallet = async () => {
    const wallet = await detectWallet();
    if (wallet) {
      return new ethers.providers.Web3Provider(wallet);
    }
    throw new Error('No Base-compatible wallet found');
  };

  const checkTransactionStatus = async (hash, provider) => {
    try {
      const receipt = await provider.getTransactionReceipt(hash);
      return receipt && receipt.status === 1;
    } catch (error) {
      console.error('Error checking transaction status:', error);
      return false;
    }
  };

  const waitForTransaction = async (hash, provider, maxAttempts = 10) => {
    let attempts = 0;
    while (attempts < maxAttempts) {
      const success = await checkTransactionStatus(hash, provider);
      if (success) return true;
      await new Promise(resolve => setTimeout(resolve, 2000));
      attempts++;
    }
    return false;
  };

  const initializeDonation = async () => {
    const provider = await waitForEthereumWallet();
    const signer = provider.getSigner();
    const donationAmountInput = document.getElementById('donationAmount');
    const donateButton = document.getElementById('donateButton');
    const predefinedButtons = document.querySelectorAll('.predefined-amount-button');

    let loading = false;

    const sendDonation = async (amount) => {
      try {
        const tx = await signer.sendTransaction({
          to: RECIPIENT,
          value: ethers.utils.parseEther(amount.toString())
        });

        const success = await waitForTransaction(tx.hash, provider);
        return success;
      } catch (error) {
        console.error('Transaction failed:', error);
        return false;
      }
    };

    const handleDonation = async (amount) => {
      if (loading) return;

      if (isNaN(amount) || amount <= 0) {
        alert('Please enter a valid amount');
        return;
      }

      loading = true;
      donateButton.disabled = true;
      donateButton.classList.add('loading');
      donateButton.textContent = 'Donating...';

      try {
        const success = await sendDonation(amount);

        if (success) {
          donateButton.classList.remove('loading');
          donateButton.textContent = 'Donation Successful';

          setTimeout(() => {
            donateButton.classList.remove('loading');
            donateButton.disabled = false;
            donateButton.textContent = 'Donate ETH';
            donationAmountInput.value = '';
          }, 3000);
        } else {
          throw new Error('Transaction failed');
        }
      } catch (error) {
        console.error('Donation error:', error);
        donateButton.classList.remove('loading');
        donateButton.disabled = false;
        donateButton.textContent = 'Donate ETH';
        alert('Transaction failed. Please try again.');
      } finally {
        loading = false;
      }
    };

    donateButton.addEventListener('click', () => {
      const amount = Number(donationAmountInput.value);
      handleDonation(amount);
    });

    predefinedButtons.forEach(button => {
      button.addEventListener('click', () => {
        const amount = Number(button.getAttribute('data-amount'));
        donationAmountInput.value = amount;
        handleDonation(amount);
      });
    });
  };

  initializeDonation();
})();

